torchscript-resnet50.inference_config:
---config.pbtxt

name: "resnet50"
platform: "pytorch_libtorch"
max_batch_size: 0
input 
[
    {
        name: "INPUT__0"
        data_type: TYPE_FP32
        format: FORMAT_NCHW
        dims: [ 3, 224, 224 ]
        reshape: {
            shape: [ 1, 3, 224, 224 ]
        }
    }
]

output 
[
    {
        name: "OUTPUT__0"
        data_type: TYPE_FP32
        dims: [ 1000 ]
        reshape: {
            shape: [ 1, 1000 ]
        }
    }
]


parameters: { key: "DISABLE_OPTIMIZED_EXECUTION" value: { string_value:"true" } }
parameters: { key: "INFERENCE_MODE" value: { string_value: "false" } }

onnx-resnet50.inference_config:
---config.pbtxt

name: "resnet50"
platform: "onnxruntime_onnx"
backend: "onnxruntime"
max_batch_size : 0

input [
  {
    name: "input_name"
    data_type: TYPE_FP32
    format: FORMAT_NCHW
    dims: [ 3, 224, 224 ]
    reshape { shape: [ 1, 3, 224, 224 ] }
  }
]
output [
  {
    name: "output_name"
    data_type: TYPE_FP32
    dims: [ 1000 ]
    reshape { shape: [ 1, 1000 ] }
  }
]

parameters { key: "intra_op_thread_count" value: { string_value: "10" } }
parameters { key: "execution_mode" value: { string_value: "1" } }
parameters { key: "inter_op_thread_count" value: { string_value: "10" } }
